mapper.select:
SELECT gateway_id, gateway_name, gateway_address, status
FROM gateway_server_detail
WHERE gateway_id = #{gatewayId} AND gateway_address = #{gatewayAddress}

mapper.update:
UPDATE gateway_server_detail
SET status = #{status}
WHERE gateway_id = #{gatewayId} AND gateway_address = #{gatewayAddress}

